import React, { useState, useRef } from "react";
import "./App.css";
import ConectysLogo from "./conectys.png";

/* ===================== ICONS ===================== */

const IconUpload = () => (
  <svg className='icon icon-large' viewBox='0 0 24 24' fill='none' stroke='currentColor'>
    <path d='M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4'></path>
    <polyline points='17 8 12 3 7 8'></polyline>
    <line x1='12' y1='3' x2='12' y2='15'></line>
  </svg>
);

const IconDownload = () => (
  <svg className='icon' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2' strokeLinecap='round' strokeLinejoin='round'>
    <path d='M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4'></path>
    <polyline points='7 10 12 15 17 10'></polyline>
    <line x1='12' y1='15' x2='12' y2='3'></line>
  </svg>
);

const IconMoon = () => (
  <svg className='icon' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2'>
    <path d='M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z' />
  </svg>
);

const IconSun = () => (
  <svg className='icon' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2'>
    <circle cx='12' cy='12' r='5' />
    <line x1='12' y1='1' x2='12' y2='3' />
    <line x1='12' y1='21' x2='12' y2='23' />
    <line x1='4.22' y1='4.22' x2='5.64' y2='5.64' />
    <line x1='18.36' y1='18.36' x2='19.78' y2='19.78' />
    <line x1='1' y1='12' x2='3' y2='12' />
    <line x1='21' y1='12' x2='23' y2='12' />
  </svg>
);

const IconGitHub = () => (
  <svg className='github-icon' viewBox='0 0 24 24' fill='currentColor'>
    <path d='M12 .5C5.73.5.5 5.74.5 12.02c0 5.11 3.29 9.45 7.86 10.98.58.11.79-.25.79-.56v-2.02c-3.2.7-3.87-1.55-3.87-1.55-.52-1.33-1.28-1.68-1.28-1.68-1.04-.71.08-.7.08-.7 1.15.08 1.75 1.18 1.75 1.18 1.02 1.75 2.67 1.24 3.32.95.1-.74.4-1.24.72-1.53-2.55-.29-5.23-1.28-5.23-5.69 0-1.26.45-2.3 1.18-3.11-.12-.29-.51-1.45.11-3.02 0 0 .97-.31 3.18 1.19a11.05 11.05 0 0 1 5.8 0c2.2-1.5 3.17-1.19 3.17-1.19.63 1.57.24 2.73.12 3.02.73.81 1.17 1.85 1.17 3.11 0 4.42-2.69 5.39-5.25 5.67.41.35.77 1.04.77 2.1v3.12c0 .31.21.67.8.56A11.52 11.52 0 0 0 23.5 12C23.5 5.74 18.27.5 12 .5z' />
  </svg>
);

const IconTrash = () => (
  <svg className='icon icon-danger' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2'>
    <polyline points='3 6 5 6 21 6'></polyline>
    <path d='M19 6l-1 14a2 2 0 0 1-2 2H8a2 2 0 0 1-2-2L5 6'></path>
    <path d='M10 11v6'></path>
    <path d='M14 11v6'></path>
  </svg>
);

const IconFile = () => (
  <svg className='icon' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2'>
    <path d='M13 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V9z'></path>
    <polyline points='13 2 13 9 20 9'></polyline>
  </svg>
);

const IconCompress = () => (
  <svg className='icon' viewBox='0 0 24 24' fill='none' stroke='currentColor' strokeWidth='2'>
    <polyline points='4 14 10 14 10 20'></polyline>
    <polyline points='20 10 14 10 14 4'></polyline>
    <line x1='14' y1='10' x2='21' y2='3'></line>
    <line x1='3' y1='21' x2='10' y2='14'></line>
  </svg>
);

/* ===================== APP ===================== */

function App() {
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("theme") === "dark");
  const [file, setFile] = useState(null);
  const [compressing, setCompressing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [originalSize, setOriginalSize] = useState(0);
  const [compressedSize, setCompressedSize] = useState(0);
  const [compressedBlob, setCompressedBlob] = useState(null);
  const [error, setError] = useState(null);
  const [quality, setQuality] = useState(0.3);

  const fileInputRef = useRef(null);
  const TARGET_SIZE = 10 * 1024 * 1024; // 10MB

  React.useEffect(() => {
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const formatBytes = bytes => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const sizes = ["Bytes", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + " " + sizes[i];
  };

  // Load libraries dynamically
  const loadLibraries = async () => {
    if (!window.pdfjsLib) {
      await new Promise((resolve, reject) => {
        const script = document.createElement("script");
        script.src = "https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.11.174/pdf.min.js";
        script.onload = () => {
          window.pdfjsLib.GlobalWorkerOptions.workerSrc = "https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.11.174/pdf.worker.min.js";
          resolve();
        };
        script.onerror = reject;
        document.head.appendChild(script);
      });
    }

    if (!window.PDFDocument) {
      await new Promise((resolve, reject) => {
        const script1 = document.createElement("script");
        script1.src = "https://cdnjs.cloudflare.com/ajax/libs/pdfkit/0.13.0/pdfkit.standalone.js";
        script1.onload = () => {
          const script2 = document.createElement("script");
          script2.src = "https://cdnjs.cloudflare.com/ajax/libs/blob-stream/0.1.3/blob-stream.js";
          script2.onload = resolve;
          script2.onerror = reject;
          document.head.appendChild(script2);
        };
        script1.onerror = reject;
        document.head.appendChild(script1);
      });
    }
  };

  const compressPDF = async (file, targetQuality) => {
    await loadLibraries();

    const arrayBuffer = await file.arrayBuffer();
    const pdf = await window.pdfjsLib.getDocument({ data: arrayBuffer }).promise;
    const numPages = pdf.numPages;

    // Create new PDF with PDFKit
    const doc = new window.PDFDocument({
      compress: true,
      autoFirstPage: false,
    });

    const stream = doc.pipe(window.blobStream());

    // Process each page
    for (let pageNum = 1; pageNum <= numPages; pageNum++) {
      const page = await pdf.getPage(pageNum);
      const viewport = page.getViewport({ scale: 1.0 });

      // Create canvas
      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");
      canvas.width = viewport.width;
      canvas.height = viewport.height;

      // Render PDF page to canvas
      await page.render({
        canvasContext: context,
        viewport: viewport,
      }).promise;

      // Convert to JPEG with target quality
      const imgData = canvas.toDataURL("image/jpeg", targetQuality);

      // Add page to new PDF
      doc.addPage({
        size: [viewport.width, viewport.height],
      });

      doc.image(imgData, 0, 0, {
        width: viewport.width,
        height: viewport.height,
      });

      setProgress(Math.round((pageNum / numPages) * 90));
    }

    doc.end();

    return new Promise(resolve => {
      stream.on("finish", function () {
        const blob = stream.toBlob("application/pdf");
        resolve(blob);
      });
    });
  };

  const handleFileSelect = async file => {
    if (!file || file.type !== "application/pdf") {
      setError("Please select a valid PDF file");
      return;
    }

    setFile(file);
    setError(null);
    setCompressedBlob(null);
    setOriginalSize(file.size);
    setCompressing(true);
    setProgress(0);

    try {
      let currentQuality = quality;
      let compressedBlob = null;

      // Try progressively lower quality until under target
      while (currentQuality > 0.05) {
        compressedBlob = await compressPDF(file, currentQuality);

        if (compressedBlob.size <= TARGET_SIZE) {
          break;
        }

        // Reduce quality and try again
        currentQuality -= 0.05;
        setProgress(90 + ((quality - currentQuality) / quality) * 10);
      }

      // If still not under target, use minimum quality
      if (compressedBlob.size > TARGET_SIZE) {
        compressedBlob = await compressPDF(file, 0.05);
      }

      setProgress(100);
      setCompressedBlob(compressedBlob);
      setCompressedSize(compressedBlob.size);
      setCompressing(false);
    } catch (err) {
      console.error(err);
      setError("Failed to compress PDF. " + err.message);
      setCompressing(false);
    }
  };

  const handleDownload = () => {
    if (!compressedBlob) return;

    const url = URL.createObjectURL(compressedBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `compressed_${file.name}`;
    link.click();
    URL.revokeObjectURL(url);
  };

  const handleReset = () => {
    setFile(null);
    setCompressedBlob(null);
    setOriginalSize(0);
    setCompressedSize(0);
    setError(null);
    setProgress(0);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const compressionRatio = originalSize > 0 ? ((1 - compressedSize / originalSize) * 100).toFixed(1) : 0;

  return (
    <div className={`app-container ${darkMode ? "dark" : ""}`}>
      <nav className='navbar'>
        <div className='nav-brand'>
          <img src={ConectysLogo} alt='Conectys Logo' className='brand-logo' />
          <span className='brand-text'>PDF Compressor</span>
        </div>

        <button className='btn-icon' onClick={() => setDarkMode(!darkMode)} aria-label='Toggle theme'>
          {darkMode ? <IconSun /> : <IconMoon />}
        </button>
      </nav>

      <main className='main-content'>
        <div className='compress-container'>
          {!file ? (
            <>
              <div className='upload-area' onClick={() => fileInputRef.current.click()}>
                <input ref={fileInputRef} type='file' accept='.pdf,application/pdf' hidden onChange={e => handleFileSelect(e.target.files[0])} />
                <IconUpload />
                <h3>Select PDF to Compress</h3>
                <p style={{ marginTop: "8px", fontSize: "0.9rem", color: "#6b7280" }}>Guaranteed under 10MB compression</p>
              </div>

              <div className='card'>
                <h3>Compression Quality</h3>
                <p style={{ fontSize: "0.875rem", color: "#6b7280", marginBottom: "1rem" }}>
                  Lower quality = smaller file size. Default starts at medium quality and reduces as needed.
                </p>
                <input
                  type='range'
                  min='0.05'
                  max='0.9'
                  step='0.05'
                  value={quality}
                  onChange={e => setQuality(parseFloat(e.target.value))}
                  style={{ width: "100%" }}
                />
                <div style={{ display: "flex", justifyContent: "space-between", fontSize: "0.875rem", color: "#6b7280", marginTop: "0.5rem" }}>
                  <span>Lowest Quality</span>
                  <span>Quality: {(quality * 100).toFixed(0)}%</span>
                  <span>Highest Quality</span>
                </div>
              </div>
            </>
          ) : (
            <div className='card compression-card'>
              <div className='file-info'>
                <IconFile />
                <div className='file-details'>
                  <h3>{file.name}</h3>
                  <p>Original size: {formatBytes(originalSize)}</p>
                </div>
                <button className='btn-icon btn-danger' onClick={handleReset} title='Remove file'>
                  <IconTrash />
                </button>
              </div>

              {compressing && (
                <div className='progress-section'>
                  <div className='progress-bar'>
                    <div className='progress-fill' style={{ width: `${progress}%` }}></div>
                  </div>
                  <p className='progress-text'>Compressing... {progress}%</p>
                </div>
              )}

              {error && (
                <div className='error-message'>
                  <p>{error}</p>
                </div>
              )}

              {compressedBlob && !compressing && (
                <div className='result-section'>
                  <div className='result-stats'>
                    <div className='stat-item'>
                      <span className='stat-label'>Compressed Size</span>
                      <span className='stat-value success'>{formatBytes(compressedSize)}</span>
                    </div>
                    <div className='stat-item'>
                      <span className='stat-label'>Reduction</span>
                      <span className='stat-value'>{compressionRatio}%</span>
                    </div>
                    <div className='stat-item'>
                      <span className='stat-label'>Target Status</span>
                      <span className='stat-value success'>âœ“ Under 10MB</span>
                    </div>
                  </div>

                  <div className='action-buttons'>
                    <button className='btn-download' onClick={handleDownload}>
                      <IconDownload />
                      <span>Download Compressed PDF</span>
                    </button>
                    <button className='btn-action' onClick={handleReset}>
                      <IconCompress />
                      <span>Compress Another</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}

          <div className='info-section'>
            <div className='info-card'>
              <h3>How it works</h3>
              <ul>
                <li>Upload your PDF file (any size)</li>
                <li>We render each page as a compressed image</li>
                <li>Quality auto-adjusts to reach under 10MB</li>
                <li>Download your compressed PDF</li>
              </ul>
            </div>

            <div className='info-card'>
              <h3>Features</h3>
              <ul>
                <li>Fast in-browser compression</li>
                <li>No data uploaded to servers</li>
                <li>Works with any PDF</li>
                <li>Adjustable quality settings</li>
              </ul>
            </div>
          </div>
        </div>
      </main>

      <footer className='footer'>
        <p>&copy; 2026 PDF Compressor. Open Source.</p>
        <a
          href='https://github.com/Joaosilva27/pdf-compressor'
          target='_blank'
          rel='noopener noreferrer'
          className='github-link'
          aria-label='GitHub Repository'
        >
          <IconGitHub />
        </a>
      </footer>
    </div>
  );
}

export default App;
