async function compressPDFUnified(pdfBytes, onProgress) {
  try {
    return await compressPDFFast(pdfBytes, onProgress);
  } catch (fastErr) {
    console.warn("Fast compression failed, falling back to rasterisation:", fastErr);
    try {
