      console.error("Raster fallback also failed:", rasterErr);
      throw new Error(`Both compression methods failed – ${rasterErr instanceof Error ? rasterErr.message : rasterErr}`);
    }
  }
}

/* ------------------- VALIDATION ------------------- */
function validatePDF(file) {
  if (!file) return {
    valid: false,
    error: "No file selected"
  };
  if (file.type !== "application/pdf" && !file.name.toLowerCase().endsWith(".pdf")) return {
    valid: false,
    error: "Please select a valid PDF file"
  };
  if (file.size === 0) return {
    valid: false,
    error: "File is empty"
  };
  if (file.size > 500 * 1024 * 1024) return {
    valid: false,
    error: "File is too large (max 500 MB)"
  };
  return {
    valid: true
  };
}

