    const copiedPages = await newPdf.copyPages(pdfDoc, pageIndices);
    copiedPages.forEach(page => {
      newPdf.addPage(page);
    });
    const pdfBytes = await newPdf.save();
    splitPdfs.push({
      bytes: pdfBytes,
      startPage: startPage + 1,
      endPage: endPage,
      name: `split_${i + 1}_pages_${startPage + 1}-${endPage}`
    });
  }
  onProgress(100, "Done!");
  return {
    splits: splitPdfs,
    totalPages,
    numSplits
