        updateMetadata: false
      });
    } catch (loadError) {
      console.error("PDF load error:", loadError);
      throw new Error("Unable to load PDF. The file may be corrupted or encrypted.");
    }
    onProgress(25, "Analyzing document structure...");

    // Get document info
    const pageCount = pdfDoc.getPageCount();
    const form = pdfDoc.getForm();
    onProgress(35, "Removing metadata and unnecessary data...");

    // Strategy 1: Remove all metadata
    try {
      pdfDoc.setTitle("");
      pdfDoc.setAuthor("");
      pdfDoc.setSubject("");
      pdfDoc.setCreator("");
      pdfDoc.setProducer("");
      pdfDoc.setKeywords([]);
      pdfDoc.setCreationDate(new Date(0));
      pdfDoc.setModificationDate(new Date(0));
    } catch (metaError) {
      console.warn("Could not remove all metadata:", metaError);
    }
    onProgress(45, "Optimizing document structure...");

